import React from 'react'
import { isauthenticated } from '../auth/authenticate'
import { useEffect, useState } from 'react'

function USERDASHBOARD() {

    const user=isauthenticated().user;
    const token=isauthenticated().token;
    const [sucess,setsucess]=useState(false)

    const deleteuser=(id)=>{
        return fetch(`http://localhost:5000/api/user/${id}`,{
            method:"DELETE",
            headers:{
                Accept:'application/json',
                "content-type":"application/json",
                token:`Bearer ${token.token}`
            }
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }

    const deleteuserbtn=(id)=>{
        deleteuser(id).then(data=>{
            if(data.error)
            {
                console.log(data)
            }
            else{
               console.log(data)
               setsucess(true)

            }
        })
    }
    const showsuccess=()=>(
        <div className="alert alert-info" style={{display:sucess ? '':'none'}}>
              User deleted!
         </div>
    );



    return (
        <div>
            {
                showsuccess()
            }
            <button type="button" className="btn btn-danger my-5" >Update</button>
            <button type="button" onClick={()=>{deleteuserbtn(user._id)}} className="btn btn-warning my-5">Delete your Account</button>
        </div>
    )
}

export default USERDASHBOARD
