import React from 'react'
import { useEffect, useState } from 'react'

function EDITPRODUCT() {
    const [product, setproduct] = useState([]);

    const getallproduct=()=>{
        return fetch('http://localhost:5000/api/product/',{
            method:"GET"
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }

    useEffect(async() => {
        const {data}=await getallproduct()
        setproduct(data)
    }, [])


    return(
        <div>

            <div class="container admin-product">

                    <div class="row mt-5">

                    {        
        product.map(item=>(
        <div class="col-md-3">
           <div class="card product-top">
           <img src={item.img} alt="" class="img-fluid"/>
               <div class="overlay">
                   <button type="button" class="btn btn-secondary" title="quick shop" >


                   </button>
                   <button type="button" class="btn btn-secondary" title="Add shop">
                       <i class=" category-icon fa-solid fa-heart"></i>
                   </button>
                   <button onClick={()=>addcart(item)} class="btn btn-secondary" title="Add to cart">
                       <i class="category-icon fa-solid fa-cart-shopping"></i>
                   </button>
               </div>
           </div>
           <div class="text-center">
               <h3>{item.title}</h3>
               <p><i class="fa fa-inr rupee" aria-hidden="true"></i>{item.price}</p>
           </div>
        </div>
       ))
        }

                    </div>    
            </div>

         </div>
    )
}

export default EDITPRODUCT
