import React from 'react'
import { isauthenticated } from '../auth/authenticate'
import { useEffect, useState } from 'react'
import { Redirect } from 'react-router';

function USERDASHBOARD() {

    const user=isauthenticated().user;
    const token=isauthenticated().token;
    const [sucess,setsucess]=useState(false)
    const [error, seterror] = useState(false)

    const deleteuser=(id)=>{
        return fetch(`http://localhost:5000/api/user/${id}`,{
            method:"DELETE",
            headers:{
                "content-type":"application/json",
                token:`Bearer ${token}`
            }
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }

    const deleteuserbtn=(id)=>{
        deleteuser(id).then(data=>{
            if(data.error)
            {
                console.log(data)
                seterror(true)
            }
            else{
               setsucess(true)
            }
        })
    }
    const showsuccess=()=>{

        if(sucess)
        {
            localStorage.removeItem("jwt")
            return <Redirect to="/" />
        }
    };

    const showerror=()=>(
        <div className="alert alert-info" style={{display:error ? '':'none'}}>
              something went wrong
         </div>
    );


    return (
        <div>
            {
                showerror()
            }
            {
                showsuccess()
            }
            <div className="mt-5">
                <a href="/dashboard">
                    <h2>
                    go back to user dashboard
                    </h2>
                </a>
            </div>
            <button type="button" className="btn btn-danger my-5" >Set new password</button>
            <button type="button" onClick={()=>{deleteuserbtn(user._id)}} className="btn btn-warning my-5">Delete your Account</button>
            <button type="button" className="btn btn-warning" data-bs-toggle="modal" data-bs-target="#exampleModal">
                 Delete user account
            </button>
            <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                         <div className="modal-header">
                              <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                         </div>
                         <div className="modal-body">
                          Do you really want to delete your account
                         </div>
                     <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                     <button type="button" className="btn btn-primary">Save changes</button>
                     </div>
                </div>
            </div>
            </div>
        </div>
    )
}

export default USERDASHBOARD
