import React from 'react'
import { Fragment } from 'react'
import './dashboard.css'
import NAV from '../components/nav'
import { isauthenticated } from '../auth/authenticate'
import { useState } from 'react'
import { Redirect } from 'react-router-dom'

function DASHBOARD() {

    const user=isauthenticated().user;
    const token=isauthenticated().token;
    const [deletesucess,setdeletesucess]=useState(false)
    const [addsuccess, setaddsuccess] = useState(false)
    const [passsuccess, setpasssuccess] = useState(false)
    const [error, seterror] = useState(false)
    const [add, setadd] = useState()
    const [newpass, setnewpass] = useState()
    const id=user._id
    const usertoken=token

  // deleting user
    const deleteuser=(id)=>{
        return fetch(`http://localhost:5000/api/user/${id}`,{
            method:"DELETE",
            headers:{
                "content-type":"application/json",
                token:`Bearer ${token}`
            }
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }
    const deleteuserbtn=(id)=>{
        deleteuser(id).then(data=>{
            if(data.error)
            {
                console.log(data)
                seterror(true)
            }
            else{
               setdeletesucess(true)
            }
        })
    }
    const showsuccess=()=>{
        if(deletesucess)
        {
            localStorage.removeItem("jwt")
            return <Redirect to="/" />
        }
    };
    const showpasssuccess=()=>(
        <div className="alert alert-info" style={{display:passsuccess ? '':'none'}}>
         Password updated succesfully.
        </div>
   );
  const showaddsuccess=()=>(
    <div className="alert alert-info" style={{display:addsuccess ? '':'none'}}>
    Address updated successfully.
    </div>
  );
    const showerror=()=>(
        <div className="alert alert-info" style={{display:error ? '':'none'}}>
              Something went wrong
         </div>
    );

    const updatepassword=(user)=>{
      return fetch(`http://localhost:5000/api/user/${id}`,{
          method:"PUT",
          headers:{
              Accept:'application/json',
              "content-type":"application/json",
              token:`Bearer ${usertoken}`
          },
          body:JSON.stringify(user)
      }).then(response=>{
          return response.json();
      }).catch(err=>{
          console.log(err)
      })
  }

    const clickupdate=(event)=>{
      event.preventDefault();
      updatepassword({username:user.username,password:newpass}).then(data=>{
       if(data.error)
       {
           console.log(data)
           seterror(true)
       }
       else{
        setpasssuccess(true)
       }

      })
   }
   const addupdate=(event)=>{
    event.preventDefault();
    updatepassword({username:user.username,address:add}).then(data=>{
     if(data.error)
     {
         console.log(data)
         seterror(true)
     }
     else{
        setaddsuccess(true)
     }

    })
 }


    return (
        <Fragment>
        <NAV/>  
            {
                showerror()
            }
            {
                showsuccess()
            }
            {
              showpasssuccess()
            }
            {
              showaddsuccess()
            }
        <div style={{paddingBottom:"150px"}}>   
        {/* padding for footer */}
            <div className="container mt-5">
                <div className="row justify-content-center">
                       <div className="col-md-8">
                       <table class="table table-light">
  <thead>
    <tr>

      <th scope="col">Your profile</th>
      <th scope="col">details</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>Username</td>
      <td>{user.username}</td>
    </tr>
    <tr>

      <td>email</td>
      <td>{user.email}</td>
    </tr>

