import React from 'react'
import { useEffect, useState } from 'react'
import { isauthenticated } from '../auth/authenticate'
function EDITPRODUCT() {
    const [product, setproduct] = useState([]);
    const [change,setchange]=useState([])
    const [sucess,setsucess]=useState(false)

    const getallproduct=()=>{
        return fetch('http://localhost:5000/api/product/',{
            method:"GET"
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }

    useEffect(async() => {
        const data=await getallproduct()
        setproduct(data)
    }, [change])

    const token=isauthenticated()

    const deleteproduct=(id)=>{
        return fetch(`http://localhost:5000/api/product/${id}`,{
            method:"DELETE",
            headers:{
                Accept:'application/json',
                "content-type":"application/json",
                token:`Bearer ${token.token}`
            }
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err);
        })
    }

    const deleteproductbtn=(id)=>{
        console.log(id)
        deleteproduct(id).then(data=>{
            if(data.error)
            {
                console.log(data)
            }
            else{
               console.log(data)
               setchange("")
               setsucess(true)
               console.log("error")
            }
        })
    }

    const showsuccess=()=>(
        <div className="alert alert-info" style={{display:sucess ? '':'none'}}>
              product deleted!
         </div>
    );

    return(
        <div>
            {
                showsuccess()
            }
            <div class="container admin-product">
                    <div class="row mt-5">
                            {
                                product.map(item=>(
                                    <div class="col-md-3">

                                 <div class="card product-top">
                                 <img src={item.img} alt="" class="img-fluid"/>
                                 </div>

                                 <div class="text-center">
                                 <button type="button" class="btn btn-outline-danger">Edit</button>
                                 <button type="button" onClick={()=>{deleteproductbtn(`${item._id}`)}} class="btn btn-outline-warning">Delete</button>
                                </div>
                               </div>
                                ))
                            }
                    </div>
            </div>

        </div>
    )
}

export default EDITPRODUCT
