import React, { Fragment } from 'react'
import './cart.css'
import {useState} from 'react'
import { useEffect } from 'react'
import NAV from '../components/nav'


function CART() {

    const iconstyle={
        fontSize:"20px",
        margin:"5px",

    }
    const [cartproduct, setcartproduct] = useState([]);
    const [cartnos, setcartnos] = useState(false)

    useEffect(() => {
        setcartnos(false)
               const fetchdata=async()=>{
                let cart=[]
                if(localStorage.getItem('products')){
                cart = JSON.parse(localStorage.getItem('products'));
                  }
                setcartproduct(cart);}
            fetchdata()
            },[cartnos])


    function plusquantity(id){
        let carts=[];

        if(typeof window!=="undefined")
           if(localStorage.getItem("products"))
           {
               carts=JSON.parse(localStorage.getItem("products"));
           }
           console.log(carts)
           carts.map((product,i)=>{
               if(product.productId === id)
               {
                if(carts[i].quantity >= 5)
                {
                    carts[i].quantity=5;
                }
                else{
                    carts[i].quantity+=1;
                }
                setcartnos(true);
               }
           });
          localStorage.setItem("products",JSON.stringify(carts));

    }
    const removeItem = productId => {
     let cart = [];
     console.log("in")
     if (typeof window !== 'undefined') {
         if (localStorage.getItem('products')) {
             cart = JSON.parse(localStorage.getItem('products'));
         }

         cart.map((product, i) => {
             if (product.productId === productId) {
                 cart.splice(i, 1);
                 console.log(product)
             }
         });

         localStorage.setItem('products', JSON.stringify(cart));
     }
     setcartnos(true);
     return cart;
 };

 const emptyCart = () => {
     if (typeof window !== 'undefined') {

         localStorage.removeItem('products');
         setcartnos(true)
     }
 };

    function minusquantity(id){
        let carts=[];

        if(typeof window!=="undefined")
           if(localStorage.getItem("products"))
           {
               carts=JSON.parse(localStorage.getItem("products"));
           }
           console.log(carts)
           carts.map((product,i)=>{
               if(product.productId === id)
               {
                    if(carts[i].quantity == 1)
                    {
                        carts[i].quantity=1;
                    }
                    else{
                        carts[i].quantity-=1;
                    }
                    setcartnos(true);
               }
           });
          localStorage.setItem("products",JSON.stringify(carts));

    }
  var total=0;
    function totalquantity(id){
     let carts=[];

     if(typeof window!=="undefined")
        if(localStorage.getItem("products"))
        {
            carts=JSON.parse(localStorage.getItem("products"));
        }
        carts.map((product,i)=>{
            total+=carts[i].quantity*carts[i].price
        });
       return total
 }


    return (
       <Fragment>
           <NAV></NAV>



           <div>
               {
                   cartproduct.length>0?  
                   <div class="container">
          <table id="cart" class="table table-hover table-condensed">
            <thead>
              <tr>
              <th style={{width:"50%"}}>Product</th>
                <th style={{width:"10%"}}>Price</th>
                <th style={{width:"8%"}}>Quantity</th>
                <th style={{width:"22%"}} class="text-center">Subtotal</th>
                <th style={{width:"10%"}}></th>
              </tr>
            </thead>
            <tbody>

            {cartproduct.map(item=>(
                <Fragment>
              <tr>
                <td data-th="Product">
                  <div class="row">
                    <div class="col-sm-2 hidden-xs"><img src={item.image} alt="..." class="img-responsive" /></div>
                    <div class="col-sm-10">
                      <h4 class="nomargin">{item.title}</h4>
                    </div>
                  </div>
                </td>
                <td data-th="Price">{item.price}</td>
                <td data-th="Quantity">

                  <button onClick={()=>plusquantity(item.productId)} class="plus-btn" type="button" name="button">
                    <img src="https://designmodo.com/demo/shopping-cart/plus.svg" alt="" />
                  </button>

                  <span className="item-quantity">{item.quantity}</span>

                  <button class="minus-btn" onClick={()=>minusquantity(item.productId)} type="button" name="button">
                    <img src="https://designmodo.com/demo/shopping-cart/minus.svg" alt="" />
                  </button>

                </td>
                <td data-th="Subtotal" class="text-center">{item.quantity*item.price}</td>
                <td class="actions" data-th="">
                <button className="trash-btn" onClick={()=>{removeItem(item.productId)}}>
                                     <i class="fa fa-trash"></i>
                                     </button>
                </td>
              </tr>
              </Fragment>
                            ))  }

            </tbody>
            <tfoot>

              <tr>
                <td><a href="#" class="btn btn-checkout"><i class="fa fa-angle-left"></i> Continue Shopping</a></td>
                <td colspan="2" class="hidden-xs"></td>
                <td class="hidden-xs text-center"><strong>Total {totalquantity()}</strong></td>
                <td><a href="#" class="btn btn-checkout btn-block">Checkout <i class="fa fa-angle-right"></i></a></td>
              </tr>
            </tfoot>
          </table>
        </div>

        :

        <div className="mt-5" style={{marginBottom:"100px"}}>
            <div className="container mt-5">
                <div className="row">
                    <h1>
                        Your Cart is empty add item in the cart
                    </h1>

                </div>
            </div>    


        </div>



    }
 </div>







