import React ,{useState}from 'react'
import './login.css'
import loginimg from '../components/static/loginimg.jpeg'


function LOGIN() {
    const [values,setvalues]=useState(
        {
            name:"",
            password:"",
            error:"",
            sucess:false
        }
    )
    const { name ,email , password ,error ,sucess}=values;

    const handlechange=name=>event=>{
    setvalues({...values,error:false,[name]:event.target.value})

    }
    const signin=(user)=>{
        return fetch('http://localhost:5000/api/auth/login',{
            method:"POST",
            headers:{
                Accept:'application/json',
                "content-type":"application/json"
            },
            body:JSON.stringify(user)
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err)
        })
    }

    const clicksubmit=(event)=>{
       event.preventDefault();
       signin({username:name,password:password})
       .then(data=>{
           if(data.errors)
           {
              setvalues({...values,error:data.error,sucess:false})
              console.log(data.errors);
           }
           else
           {
               setvalues({...values,
            name:'',
            email:'',
            password:'',
            error:'',
            sucess:true,
              })
           }

       })
    }
    const showerror=()=>(
        <div className="alert alert-info" style={{display:error ? '':'none'}}>
              {error}
         </div>
    );
    const showsuccess=()=>(
        <div className="alert alert-info" style={{display:sucess ? '':'none'}}>
              new account created! please Signin
         </div>
    );

    return (
        <div>
        <div className="container login-container">
        <div className="row login-row">
            <div className="col-lg-5 login-img-container">
                      <img src={loginimg} alt="" className="img-fluid login-img"/>
            </div>
            <div className="col-lg-7 text-center py-5">
                <h1 style={{color:'#0062cc'}}>
                    Login
                </h1>  
                <form>
                    <div className="form-row py-3 pt-5">
                        <div className="offset-1 col-lg-10">
                            <input onChange={handlechange('name')} type="text" className="login-input" placeholder="Username"/>
                        </div>
                    </div>
                    <div className="form-row">
                      <div className="offset-1 py-3 col-lg-10">
                          <input onChange={handlechange('password')} type="Password" className="login-input" placeholder="**********"/>
                      </div>
                    </div>
                    <div className="form-row py-3">
                      <div className="offset-1 col-lg-10">
                          <button onClick={clicksubmit} className="login-btn">LogIn</button>
                      </div>
                    </div>
                    </form>
            </div>
        </div>
        </div>
        </div>
    )
}

export default LOGIN
