import React from 'react'
import axios from 'axios'
import { useState,useEffect } from 'react'
import image1 from './static/image1.jpg'
import image2 from './static/image2.jpg'
import image3 from './static/image3.jpg'
import { Link } from 'react-router-dom'
import { addProduct } from './redux/cartredux'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import { addItem } from '../cart/carthelper'
import { Redirect } from 'react-router'
import './menu.css'


function MENU() {
  const [product, setproduct] = useState([]);
  const [redirect, setredirect] = useState(false)
  const quantity = useSelector(state => state.cart.quantity) 
  console.log(quantity)
  const dispatch=useDispatch();

  useEffect(async() => {
    const fetchdata=async()=>{
        const {data}=await axios.get(`http://localhost:5000/api/product?new=4`)
        setproduct(data)
        }
        fetchdata()
     }, [])

     const c = useSelector(state => state.cart) 
     console.log(c)

     function handleclick(item)
     {
        addItem(product,()=>{
          setredirect(true)
        })
     }
     const shouldRedirect = redirect => {
      if (redirect) {
        return <Redirect to="/cart" />;
      }
    };
      /*
      function handleclick(item,title)
      { 
        dispatch(addProduct({item,quantity}))
          let products = [];
          if(localStorage.getItem('products')){
              products = JSON.parse(localStorage.getItem('products'));
          }
          if(products.length==0)
               {
                  products.push({'productId' : item._id, 'image' : item.img ,'price':item.price ,'title' :item.title,'quantity':1 });
                  localStorage.setItem('products', JSON.stringify(products));
               }
          else{
            if(products.some((p)=> p.productId === item._id))
             {  
                  let cart=[];
                  if(typeof window!=="undefined")
                  if(localStorage.getItem("products"))
                   {
                     cart=JSON.parse(localStorage.getItem("products"));
                   }
                  cart.map((product,i)=>{
                  if(product.title === title)
                  { 
                   var a=cart[i].quantity;
                   cart[i].quantity=a+1;
                  }
                  });
                  localStorage.setItem("products",JSON.stringify(cart));
              }

             else{
              products.push({'productId' : item._id, 'image' : item.img ,'price':item.price ,'title' :item.title,'quantity':1});
              localStorage.setItem('products', JSON.stringify(products));
               }

          } 

      }

      */
  return (
    <div>
      {
        shouldRedirect()
      }
    <div id="carouselExampleCaptions" className="carousel slide" data-bs-ride="carousel">
    <div className="carousel-indicators">
      <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
      <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="1" aria-label="Slide 2"></button>
      <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="2" aria-label="Slide 3"></button>
    </div>
    <div className="carousel-inner">
      <div className="carousel-item active">
        <img src={image1} className="d-block w-100 xyz" alt="..."/>
        <div className="carousel-caption d-none d-md-block">

          <div className="container ">
            <div className="row justify-content-start text-left">
              <div class="col-lg-8 mx-auto">
              <div className="animated animatedFadeInUp fadeInUp">
                <p>
                  WINTER COLLECTION 2020
                </p>
                <h1>
                  Get up to 30% off
                </h1>
                <h1>New Arrival</h1>
                </div>
                <button className="slider-btn">
                  SHOP NOW
                </button>
              </div>
            </div>

          </div>
        </div>
      </div>
      <div className="carousel-item">
        <img src={image2} className="d-block w-100 xyz " alt="..."/>
        <div className="carousel-caption d-none d-md-block">

        <div className="container">
            <div className="row justify-content-start text-left">
              <div class="col-lg-8 mx-auto">
              <div className="animated animatedFadeInUp fadeInUp">
                <p>
                  WINTER COLLECTION 2020
                </p>
                <h1>
                  Get up to 30% off
                </h1>
                <h1>New Arrival</h1>
                </div>
                <button className="slider-btn">
                  SHOP NOW
                </button>
              </div>
            </div>

          </div>
        </div>
      </div>
      <div className="carousel-item">
        <img src={image3} className="d-block w-100 xyz" alt="..."/>
        <div className="carousel-caption d-none d-md-block">
        <div className="container">
            <div className="row justify-content-start text-left">
              <div class="col-lg-8 mx-auto">
              <div className="animated animatedFadeInUp fadeInUp">
                <p>
                  WINTER COLLECTION 2020
                </p>
                <h1>
                  Get up to 30% off
                </h1>
                <h1>New Arrival</h1>
                </div>
                <button className="slider-btn">
                  SHOP NOW
                </button>
              </div>
            </div>

          </div>
        </div>
      </div>
    </div>
    <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="prev">
      <span className="carousel-control-prev-icon" aria-hidden="true"></span>
      <span className="visually-hidden">Previous</span>
    </button>
    <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="next">
      <span className="carousel-control-next-icon" aria-hidden="true"></span>
      <span className="visually-hidden">Next</span>
    </button>
  </div>
