import React, { Fragment } from 'react'
import NAV from '../components/nav'
import { useState,useEffect } from 'react'
import axios from 'axios'
import { isauthenticated } from '../auth/authenticate'

function ORDER() {

    const [order, setorder] = useState([])
    const [status, setstatus] = useState("")

    const token=isauthenticated().token;
    useEffect(() => {
        const fetchdata=async()=>{
            const {data}=await axios.get('http://localhost:5000/api/order/',{
                headers: {
                   token:`Bearer ${token}`
                  }
            })
            setorder(data)
            }
            fetchdata()
    }, [])
  /*
    const updateorderapi=async(user)=>{
        return fetch(`http://localhost:5000/api/user/${id}`,{
            method:"PUT",
            headers:{
                Accept:'application/json',
                "content-type":"application/json",
                token:`Bearer ${usertoken}`
            },
            body:JSON.stringify(user)
        }).then(response=>{
            return response.json();
        }).catch(err=>{
            console.log(err)
        })
    }*/

      const orderupdate=(item)=>{
        console.log("item")
        console.log(item)

       /*updateoderapi({username:user.username,password:newpass}).then(data=>{
         if(data.error)
          {
              console.log(data)

          }
          else{

          }

         })*/

     }


    return (
       <Fragment>
            <NAV></NAV>
        <div className='container text-center'>
            <h2>orders page</h2>
            <div className='row justify-content-evenly'>

           {
               order.map((item,index)=>(
                <div class="card mt-4 mb-4" style={{width: "18rem"}}>
                <div class="card-body">
                    <h5 class="card-title">Order {index+1}</h5>
                    <h6>Payment Id-{item.paymentid}</h6>
                    <h6>Amount-{item.amount}</h6>
                    <h6>Address-{item.address}</h6>
                    <h6>Status-{item.status}</h6>
                    <button onClick={()=>{orderupdate(item)}} data-bs-toggle="modal" data-bs-target="#exampleModal" className='btn btn-danger mt-2 mb-2'>Edit status</button>


                    <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                      <div class="modal-dialog">
                       <div class="modal-content">
                         <div class="modal-header">
                          <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                           <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                             </div>
                               <div class="modal-body">
                                 <select onChange={(event)=>setstatus(event.target.value)}>
                                     <option value="Pending">
                                            Pending
                                     </option>
                                     <option value="Delivered">
                                         Delivered
                                     </option>
                                     <option value="On the way">
                                         On the way
                                     </option>
                                 </select>
                               </div>
                               <div class="modal-footer">
                              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                              <button type="button" class="btn btn-primary">Save changes</button>
                           </div>
                         </div>
                        </div>
                      </div>
                </div>
               </div>
               ))
           }





            </div> 
        </div>
       </Fragment>
    )
}

export default ORDER
